mapper.select:
select available_integral
from school_integral
where is_deleted = 0 and user_id = #{userId}

select id,user_id,total_integral,available_integral
from school_integral
where is_deleted = 0 and user_id = #{userId}

mapper.update:
update school_integral
set available_integral = available_integral - #{totalCost}
where id = #{id}

update school_integral
set total_integral = total_integral - #{operateIntegral}
where id = #{integralId}

update school_integral_record
set status = #{code}
where business_id = #{orderId}

update school_integral
set total_integral = total_integral + #{totalCost},available_integral = available_integral + #{totalCost}
where user_id = #{riderId}

mapper.select:
select id
from school_integral
where user_id = #{riderId}
