mapper.select:
select id,parent_id,name,address,description,type,create_time,update_time,name_en,icon from school_resources where is_deleted = 0
and name = #{name}
order by update_time desc

mapper.delete:
update school_resources
set is_deleted = 1
where id = #{id}

mapper.update:
update school_resources
set name = #{name},address = #{address},description = #{description},
type = #{type},parent_id = #{parentId},name_en = #{nameEn},icon = #{icon}
where id = #{id}
