mapper.select:
select id,name,address,description,type,create_time from school_resources where is_deleted = 0
and name = #{name}
order by id desc

mapper.delete:
update school_resources
set is_deleted = 1
where id = #{id}

mapper.update:
update school_resources
set name = #{name},address = #{address},description = #{description},type = #{type}
where id = #{id}
