mapper.select:
select id,name,order_index,description,type,create_time,update_time from school_role where is_deleted = 0
and name = #{name}
order by update_time desc

mapper.delete:
update school_role
set is_deleted = 1
where id = #{id}

mapper.update:
update school_role
set name = #{name},order_index = #{orderIndex},type = #{type},description = #{description}
where id = #{id}
