mapper.select:
select id,title,content,url,creator,create_time,update_time,type
from school_news
where is_deleted=0

    and title = #{title}


    and type = #{type}

order by id desc

mapper.delete:
update school_news
set is_deleted = 1
where id = #{id}

mapper.update:
update school_news
set title = #{title},content = #{content},url = #{url},type = #{type}
where id = #{id}
